				$files = array();
				$handle=opendir('html/prototype');
				while (false !== ($file = readdir($handle))):
				    if(stristr($file,'.html')):
				        $files[] = $file;
				    endif;
				endwhile;
				sort($files);

				echo '<h2>Example pages</h2>';
				echo '<ul>';
				foreach ($files as $file):
					$fullfilename = pathinfo($file);
					$filename = $fullfilename['filename'];
					echo '<li><a href="html/prototype/'.$file.'">'.$filename.'</a></li>';
				endforeach;
				echo '</ul>';

				?>
		</div>

	</body>
